import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as t,e as _}from"./app-33116eeb.js";const o={},c=_("<p>田家历﹝元﹞俞宗本撰</p><p>正月</p><p>竖篱落粪田开荒修蚕屋织蚕箔造桑机</p><p>造麻鞋春米[此月人闲]筑墙</p><p>二月</p><p>栽柳舒蒲桃上架解栗裹缚云石榴裹缚</p><p>造酱[是月合为中时]寒食前后收柴炭造布浣冬衣采桑螵蛸</p><p>三月</p><p>利沟渎葺垣墙治屋室以待霖雨脱击移茄子</p><p>造酪[是月牛羊饱革好造也]</p><p>四月</p><p>收蔓菁芥萝卜等子收干椹子锄葱收干笋藏笋</p><p>四月伐木不蛀修防防开水窦正屋漏以备暴雨</p><p>五月</p><p>藏毛羽物毡须人卧不卧晴则晒帚扫</p><p>收种豌豆蜀芥胡妥子</p><p>六月</p><p>命女工织紬绢收芥子[中秋后]收花药子[便种之]</p><p>收李核[便种]收苜蓿收槐花[曝干]</p><p>斫竹[此月及八月不蛀]沤麻晒毡褥书裘</p><p>种小蒜[同七月]萝葡</p><p>七月</p><p>收楮子浣故衣制新衣作夹衣以被始凉刈蒿草</p><p>种蜀芥分薤沤晚麻耕菜地收荷叶阴干</p><p>拭漆器五月至此月尽经雨后漆器图画箱筪须晒干则不损收瓜蒂收蒺藜[同八月]</p><p>八月</p><p>收薏苡收角蒿收韭花收胡桃收枣子开蜜</p><p>下旬造油衣收油麻秫江豆备冬衣刈莞蔁</p><p>九月</p><p>收豕[同十月]收皂角贮麻子油采菊花收木瓜</p><p>备冬藏凡蔓菁荏蓼韭辈脆美而不耐停若旱园菜稍硬停得直至二月</p><p>十月</p><p>穿垣墙墐北户缚荐遮掩牛马屋收槐实梓实</p><p>收牛膝地黄造牛衣盘瘗蒲桃</p><p>包裹栗树石榴树不尔即冻死收诸般谷种大小豆种</p><p>十一月</p><p>货薪柴绵絮伐木取竹箭此月坚成造什物农具</p><p>折麻放麻刈蒿棘</p><p>贮年支草于隙地至六月及秋霖时俱利倍</p><p>十二月</p><p>造车贮雪水收腊糟粪地刈棘屯啬造农器</p><p>收羔种收牛粪</p>",42),r=[c];function s(n,a){return e(),t("div",null,r)}const f=p(o,[["render",s],["__file","田家历.html.vue"]]);export{f as default};
